import React from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import moment from 'moment'

class Loans extends React.Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    componentDidMount() {
        fetch('/react-interview/getLoanData')
            .then(res => {
                return res.json()
            })
            .then(loans => {
                this.setState({
                    loans: loans,
                })
            });
    }

    render() {
        const data = this.state.loans

        const columns = [{
            Header: 'Loan ID',
            accessor: 'Loan_ID',
        }, {
            Header: 'Gender',
            accessor: 'Gender'
        }, {
            Header: 'Age',
            accessor: 'city'
        }, {
            Header: 'Date',
            accessor: 'date',
            Cell: props => <span>{moment(props.value).format("MMM Do YY")}</span> 
        }, {
            Header: 'Time',
            accessor: 'time'
        }, {
            Header: 'Area',
            accessor: 'area'
        }, {
            Header: 'Direction',
            accessor: 'direction',
        }, {
            Header: 'Depth',
            accessor: 'depth'
        }, {
            Header: 'Distance (km)',
            accessor: 'dist'
        }, {
            Header: 'Latitude',
            accessor: 'lat'
        }, {
            Header: 'Longitude',
            accessor: 'mb'
        }, {
            Header: 'MS',
            accessor: 'ms'
        }, {
            Header: 'MB',
            accessor: 'mb'
        }, {
            Header: 'XM',
            accessor: 'xm'
        }, {
            Header: 'MD',
            accessor: 'md',
        }, {
            Header: 'MW',
            accessor: 'mw',
        }]

        return <ReactTable
            data={data}
            columns={columns}
        />
    }

}

export default Loans;
