import React, { Component } from "react";
import { compose, withProps } from "recompose";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker
} from "react-google-maps";

const apiKey = 'AIzaSyBdncZvkNUpZiLS-07md2po65CCB9SzrH4'

const MyMapComponent = compose(
  withProps({
    googleMapURL:
      `https://maps.googleapis.com/maps/api/js?key=${apiKey}&v=3.exp&libraries=geometry,drawing,places`,
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />,
    mapElement: <div style={{ height: `100%` }} />
  }),
  withScriptjs,
  withGoogleMap
)(props => (
  <GoogleMap 
  defaultZoom={8} 
  defaultCenter={{ lat: -34.397, lng: 150.644 }}>
    {props.isMarkerShown && (
      <Marker position={{ lat: -34.397, lng: 150.644 }} />
    )} 
  </GoogleMap>
));

export default class GoogleMapComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    componentDidMount() {
        fetch('/react-interview/getLowesStores')
          .then(res => {
              return res.json()
           })
          .then(locations => { 
              console.log(locations); 
              const markers = locations.map((site, i) => {
                const markerLatLng = {
                    position: {
                        lat: site.geometry.location.lat,
                        lng: site.geometry.location.lng
                    }
                }
                return (
                    <Marker
                    key={i}
                    {...markerLatLng}
                    site={site}>
                    </Marker>
                )
              })
              this.setState({ markers: markers })
           });
       }

    render() {
        return (
            <div>
                <MyMapComponent
                isMarkerShown
                markers={this.state.markers}
                />
                </div>
        )
    }

}
